package authorizedkeys

import (
	"strings"
	"testing"

	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

func TestAuthorizedKeyParser(t *testing.T) {

	content := `
# Comments allowed at start of line
ssh-rsa  user@example.net
from="*.sales.example.net,!pc.sales.example.net" ssh-rsa  john@example.net
command="dump /home",no-pty,no-port-forwarding ssh-rsa  example.net
permitopen="192.0.2.1:80",permitopen="192.0.2.2:25" ssh-rsa 
permitlisten="localhost:8080",permitopen="localhost:22000" ssh-rsa 
tunnel="0",command="sh /etc/netstart tun0" ssh-rsa  jane@example.net
restrict,command="uptime" ssh-rsa  user@example.net
restrict,pty,command="nethack" ssh-rsa  user@example.net
no-touch-required sk-ecdsa-sha2-nistp256@openssh.com  user@example.net
`

	entries, err := Parse(strings.NewReader(content))
	require.NoError(t, err)
	assert.Equal(t, 9, len(entries))
	assert.Equal(t, "user@example.net", entries[0].Label)
	assert.Equal(t, "ssh-rsa", entries[0].Key.Type())

	assert.Equal(t, "", entries[0].Base64Key())
}
